import React, { useEffect, useState } from "react";
import { useLazyQuery, useQuery } from "@apollo/client";
import { LOGGED_USER, ALL_BOOKS } from "../queries";

const Recommendations = (props) => {
  const [book, setBooks] = useState();
  const [genre, setGenre] = useState();
  const user = useQuery(LOGGED_USER);

  const [getBooks, bookResult] = useLazyQuery(ALL_BOOKS);

  useEffect(() => {
    if (user.data && user.data.me !== null) {
      setGenre(user.data.me.favoriteGenre);
      getBooks({ variables: { genre } });
    }
  }, [genre, getBooks, user.data]);

  //const genre = user.data && user.data.me ? user.data.me.favoriteGenre : null;

  useEffect(() => {
    if (bookResult.data) {
      setBooks(bookResult.data.allBooks);
    }
  }, [bookResult.data]);

  if (user.loading) {
    return <div>loading...</div>;
  }

  if (!props.show) {
    return null;
  }

  return (
    <div>
      <h2>Recommendations</h2>
      <div>
        <p>
          books in your favorite genre <strong>{genre}</strong>{" "}
        </p>
      </div>
      <table>
        <tbody>
          <tr>
            <th>title</th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {book.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Recommendations;
