import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import { ALL_AUTHORS, EDIT_AUTHOR_BORNYEAR } from "../queries";

const Authors = ({ setError, show }) => {
  const [name, setName] = useState("");
  const [bornyear, setBornyear] = useState("");

  const result = useQuery(ALL_AUTHORS);

  const [editAuthor, authors] = useMutation(EDIT_AUTHOR_BORNYEAR, {
    refetchQueries: [{ query: ALL_AUTHORS }],
    onError: (error) => {
      setError(error.graphQLErrors[0].message);
    },
  });

  useEffect(() => {
    if (authors.data && authors.data.editAuthor === null) {
      console.log("Something went wrong");
      console.log(authors.data);
      console.log(authors.data.editAuthor);
    }
  }, [authors.data]);

  if (result.loading) return <div>wait..loading...</div>;

  if (!show) return null;

  const handleSubmit = async (event) => {
    event.preventDefault();
    editAuthor({ variables: { name, bornyear } });
    setName();
    setBornyear();
  };

  return (
    <div>
      <div>
        <h2>Authors</h2>
        <table>
          <tbody>
            <tr>
              <th>name</th>
              <th>born</th>
              <th>books</th>
            </tr>
            {result.data.allAuthors.map((a) => (
              <tr key={a.name}>
                <td>{a.name}</td>
                <td>{a.born}</td>
                <td>{a.bookCount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div>
        <h3>Set birthyear</h3>
        <form onSubmit={handleSubmit}>
          <div>
            <select
              value={name}
              onChange={({ target }) => setName(target.value)}
            >
              <option value={null}> </option>
              {result.data.allAuthors.map((a) => (
                <option key={a.name} value={a.name}>
                  {a.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <input
              value={bornyear}
              onChange={({ target }) => setBornyear(Number(target.value))}
              placeholder="set year"
            />
          </div>
          <br></br>
          <button type="submit">update author</button>
        </form>
      </div>
    </div>
  );
}; //end

export default Authors;
