import React, { useState } from "react";
import { useQuery } from "@apollo/client";
import { ALL_BOOKS } from "../queries";

const Books = (props) => {
  const [filter, setFilter] = useState();

  let result = useQuery(ALL_BOOKS);

  if (result.loading) {
    return <div>loading...</div>;
  }

  if (!props.show) {
    return null;
  }

  let genresList = [];
  result = result.data.allBooks;

  result.map((book) =>
    book.genres.foreach((genre) => {
      genresList.push(genre);
    })
  );
  genresList = [...new Set(genresList)];

  return (
    <div>
      <h2>Books</h2>
      <div>
        <p>placeholder </p>
      </div>

      <table cellSpacing="5">
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {result.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
              <td>{a.genres.join(", ")} </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Books;
