import React, { useEffect, useState } from "react";
import { useLazyQuery, useQuery } from "@apollo/client";
import { LOGGED_USER, ALL_BOOKS, ALLBOOKSGENRES } from "../queries";

const Recommendations = (props) => {
  const [book, setBooks] = useState();
  const [user, setUser] = useState();

  const [loggedUser, result] = useLazyQuery(LOGGED_USER);
  const [getBooks, bookResult] = useLazyQuery(ALLBOOKSGENRES);

  useEffect(() => {
    if (result.data) {
      setUser(result.data.me);
    }
  }, [result]); //eslint-disable-line

  useEffect(() => {
    if (loggedUser.data) {
      getBooks({ variables: { genre: user.data.me.favoriteGenre } });
    }
  }, [loggedUser.data]); //eslint-disable-line

  useEffect(() => {
    if (bookResult) {
      setBooks(bookResult.data.allBooks);
    }
  }, [bookResult]);

  if (!props.show) {
    return null;
  }

  return (
    <div>
      <h2>Recommendations</h2>
      <div>
        <p>
          books in your favorite genre <strong>{user.favoriteGenre}</strong>{" "}
        </p>
      </div>
      <table>
        <tbody>
          <tr>
            <th>title</th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {book.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Recommendations;
