import React, { useState, useEffect } from "react";
import {
  useApolloClient,
  useSubscription,
  useLazyQuery,
  useQuery,
} from "@apollo/client";
import Authors from "./components/Authors";
import Books from "./components/Books";
import NewBook from "./components/NewBook";
import LoginForm from "./components/LoginForm";
import Recommendations from "./components/Recommendations";
import { BOOK_ADDED, ALL_BOOKS, LOGGED_USER } from "./queries";
//import "bootstrap/dist/css/bootstrap.min.css";

const Notify = ({ errorMessage }) => {
  if (!errorMessage) {
    return null;
  }
  return <div style={{ color: "red" }}>{errorMessage}</div>;
};

const App = () => {
  const [page, setPage] = useState("authors");
  const [token, setToken] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [user, setUser] = useState();

  const [loggedUser, result] = useLazyQuery(LOGGED_USER);

  useEffect(() => {
    if (result.data) {
      setUser(result.data.me);
    }
  }, [result]);

  const client = useApolloClient();

  const notify = (message) => {
    setErrorMessage(message);
    setTimeout(() => {
      setErrorMessage(null);
    }, 10000);
  };

  useEffect(() => {
    const token = localStorage.getItem("user-token");
    return !token ? null : setToken(token);
  }, []);

  const login = (token) => {
    setToken(token);
    localStorage.setItem("user-token", token);
    setPage("authors");
  };

  const logout = () => {
    setToken(null);
    localStorage.clear();
    client.resetStore();
    setPage("authors");
  };

  const updateCacheWith = (addedBook) => {
    const includedIn = (set, object) =>
      set.map((p) => p.id).includes(object.id);

    const dataInStore = client.readQuery({ query: ALL_BOOKS });
    if (!includedIn(dataInStore.allBooks, addedBook)) {
      client.writeQuery({
        query: ALL_BOOKS,
        data: { allBooks: dataInStore.allBooks.concat(addedBook) },
      });
    }
  };

  useSubscription(BOOK_ADDED, {
    onSubscriptionData: ({ subscriptionData }) => {
      const addedBook = subscriptionData.data.bookAdded;
      notify(`${addedBook.title} was just added to the library!`);
      updateCacheWith(addedBook);
      console.log(subscriptionData);
    },
  });

  return (
    <div>
      <Notify errorMessage={errorMessage} />
      <div>
        <button onClick={() => setPage("authors")}>authors</button>
        <button onClick={() => setPage("books")}>books</button>
        {token && (
          <button
            onClick={() => {
              loggedUser();
              setPage("rec");
            }}
          >
            recommendations
          </button>
        )}
        {token && <button onClick={() => setPage("add")}>add book</button>}
        {!token && <button onClick={() => setPage("login")}>login</button>}
        {token && <button onClick={logout}>logout</button>}
      </div>
      <Authors show={page === "authors"} setError={notify} />
      <Books show={page === "books"} />
      <NewBook show={page === "add"} setError={notify} />

      {user && (
        <Recommendations show={page === "rec"} setError={notify} user={user} />
      )}

      <LoginForm show={page === "login"} setError={notify} setToken={login} />
    </div>
  );
};

export default App;
