import React, { useState, useEffect } from "react";
import { useApolloClient, useLazyQuery } from "@apollo/client";
import Authors from "./components/Authors";
import Books from "./components/Books";
import NewBook from "./components/NewBook";
import LoginForm from "./components/LoginForm";
import Recommendations from "./components/Recommendations";
import { LOGGED_USER } from "./queries";
//import "bootstrap/dist/css/bootstrap.min.css";

const Notify = ({ errorMessage }) => {
  if (!errorMessage) {
    return null;
  }
  return <div style={{ color: "red" }}>{errorMessage}</div>;
};

const App = () => {
  const [page, setPage] = useState("authors");
  const [token, setToken] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [user, setUser] = useState("");

  const client = useApolloClient();

  const [useri, result] = useLazyQuery(LOGGED_USER, {
    onError: (error) => {
      notify(error.graphQLErrors[0].message);
    },
  });

  const notify = (message) => {
    setErrorMessage(message);
    setTimeout(() => {
      setErrorMessage(null);
    }, 10000);
  };

  useEffect(() => {
    const token = localStorage.getItem("user-token");
    return !token ? null : setToken(token);
  }, []);

  useEffect(() => {
    if (result.data) {
      setUser(result.data.me);
    }
  }, [result]);

  const login = (token) => {
    setToken(token);
    localStorage.setItem("user-token", token);
    setPage("authors");
  };

  const logout = () => {
    setToken(null);
    localStorage.clear();
    client.resetStore();
    setPage("authors");
  };

  return (
    <div>
      <Notify errorMessage={errorMessage} />
      <div>
        <button onClick={() => setPage("authors")}>authors</button>
        <button onClick={() => setPage("books")}>books</button>
        {token && (
          <button onClick={() => setPage("rec")}>recommendations</button>
        )}
        {token && <button onClick={() => setPage("add")}>add book</button>}
        {!token && <button onClick={() => setPage("login")}>login</button>}
        {token && <button onClick={logout}>logout</button>}
      </div>
      <Authors show={page === "authors"} setError={notify} />
      <Books show={page === "books"} />
      <NewBook show={page === "add"} setError={notify} />

      <Recommendations show={page === "rec"} setError={notify} user={useri} />
      <LoginForm show={page === "login"} setError={notify} setToken={login} />
    </div>
  );
};

export default App;
