import React, { useEffect, useState } from "react";
import { useQuery, useLazyQuery } from "@apollo/client";
import { ALL_BOOKS, BOOKS_GENRE, LOGGED_USER } from "../queries";

const Recommendations = ({ user, props }) => {
  const [books, setBooks] = useState(null);

  const booksByFavoriteGenre = useQuery(ALL_BOOKS, {
    variables: { genre: user.favoriteGenre },
  });

  useEffect(() => {
    if (booksByFavoriteGenre.data) {
      setBooks(booksByFavoriteGenre.data.allBooks);
    }
  }, [booksByFavoriteGenre.data]);

  if (!props.show) {
    return null;
  }

  if (!books) {
    return <div>loading...</div>;
  }

  return (
    <div>
      <h2>Recommendations</h2>
      <div>
        <p>
          books in your favorite genre <strong></strong>
        </p>
      </div>
      <table>
        <tbody>
          <tr>
            <th>title</th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {books.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
              <td>{a.genres}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Recommendations;
