import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import { ALL_AUTHORS, EDIT_AUTHOR_BORNYEAR } from "../queries";

const Authors = (props) => {
  const [name, setName] = useState("");
  const [born, setBorn] = useState("");
  const [num, setNum] = useState(0);

  const result = useQuery(ALL_AUTHORS);

  const [changeYear, results] = useMutation(EDIT_AUTHOR_BORNYEAR, {
    refetchQueries: [{ query: ALL_AUTHORS }],
  });

  const submit = async (event) => {
    event.preventDefault();
    changeYear({ variables: { name, born } });
    setName("");
    setBorn("");
  };

  useEffect(() => {
    if (results.data && results.data.editAuthor === null) {
      console.log("Something went wrong when changing birth year");
      console.log(results.data);
      console.log(results.data.editAuthor);
    }
  }, [results.data]);

  if (result.loading) {
    return <div>loading...</div>;
  }

  if (!props.show) {
    return null;
  }

  return (
    <div>
      <div>
        <h2>Authors</h2>
        <table cellSpacing="10">
          <tbody>
            <tr>
              <th></th>
              <th>born</th>
              <th>books</th>
            </tr>
            {result.data.allAuthors.map((a) => (
              <tr key={a.name}>
                <td>{a.name}</td>
                <td>{a.born}</td>
                <td>{a.bookCount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div>
        <h2>Set birthyear</h2>

        <form onSubmit={submit}>
          <div>
            <select
              value={name}
              onChange={({ target }) => setName(target.value)}
            >
              <option value={null}> </option>
              {result.data.allAuthors.map((a) => (
                <option key={a.name} value={a.name}>
                  {a.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <input
              value={born}
              type="number"
              onChange={(e) => setNum(parseInt(e.target.value) || num)}
              placeholder="set year"
            />
          </div>
          <br></br>
          <button type="submit">update author</button>
        </form>
      </div>
    </div>
  );
}; //end

export default Authors;
