import React, { useState } from "react";
import { useQuery } from "@apollo/client";
import { ALL_BOOKS } from "../queries";

const Books = (props) => {
  const [genreFilter, setgenreFilter] = useState();

  const result = useQuery(ALL_BOOKS);

  if (result.loading) {
    return <div>loading...</div>;
  }

  let genresList = [];
  result.data.allBooks.map((b) =>
    b.genres.forEach((g) => {
      genresList.push(g);
      genresList = [...new Set(genresList)]; //generates a unique set without duplicates
    })
  );

  let listed = [];
  if (genreFilter) {
    listed = result.data.allBooks.filter((b) => b.genres.includes(genreFilter));
  } else {
    listed = result.data.allBooks;
  }

  if (!props.show) {
    return null;
  }

  return (
    <div>
      <h2>Books</h2>
      <div>
        <p>in genre {genreFilter} </p>
      </div>

      <table cellSpacing="5">
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {listed.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
              <td>{a.genres.join(", ")} </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={() => setgenreFilter(null)}>all</button>
      {genresList.map((g) => (
        <button onClick={() => setgenreFilter(g)} key={g} value={g}>
          {g}
        </button>
      ))}
    </div>
  );
};

export default Books;
