import React, { useEffect } from "react";
import { useLazyQuery, useQuery } from "@apollo/client";
import { ALLBOOKSGENRES, LOGGED_USER } from "../queries";

const Recommendations = (props) => {
  const result = useQuery(LOGGED_USER);

  const [getBooks, booksresult] = useLazyQuery(ALLBOOKSGENRES);

  useEffect(() => {
    if (result.data) {
      getBooks({ variables: { genre: result.data.me.favoriteGenre } });
    }
  }, [getBooks, result.data]);

  if (result.loading) {
    return <div>loading...</div>;
  }

  if (!props.show) {
    return null;
  }

  return (
    <div>
      <h2>Recommendations</h2>
      <div>
        <p>
          book in your favorite genre{" "}
          <strong>{result.data.me.favoriteGenre}</strong>{" "}
        </p>
      </div>
      <table>
        <tbody>
          <tr>
            <th>title</th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {booksresult.data.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
              <td>{a.genres.join(", ")} </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Recommendations;
