import React, { useState } from "react";
import { useQuery } from "@apollo/client";
import { ALL_BOOKS } from "../queries";

const Books = (props) => {
  const [genreFilter, setgenreFilter] = useState("");

  const result = useQuery(ALL_BOOKS);

  if (result.loading) <div>wait..loading...</div>;

  let genresFilter = [];
  result.data.allBooks.map((b) =>
    b.genres.map((g) => {
      genresFilter.push(g);
      return (genresFilter = [...new Set(genresFilter)]); //generates a unique set without duplicates
    })
  );

  if (!props.show) return null;

  const booksFilter = !genreFilter
    ? result.data.allBooks
    : result.data.allBooks.filter((b) => b.genres.includes(genreFilter));

  return (
    <div>
      <h2>Books</h2>
      <div>
        {genreFilter ? (
          <p>
            in genre <strong>{genreFilter}</strong>
          </p>
        ) : (
          <p></p>
        )}
      </div>
      <table>
        <tbody>
          <tr>
            <th>title</th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {booksFilter.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
              <td>{a.genres.join(", ")} </td>
            </tr>
          ))}
        </tbody>
      </table>
      <br></br>
      {genresFilter.map((b) => (
        <button onClick={() => setgenreFilter(b)} key={b} value={b}>
          {b}
        </button>
      ))}
      <button onClick={() => setgenreFilter("")}>All</button>
    </div>
  );
};

export default Books;
