import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import Select from "react-select";
import { ALL_AUTHORS, EDIT_AUTHOR_BORNYEAR } from "../queries";

const BirthEdit = (props) => {
  const [name, setName] = useState("");
  const [born, setBorn] = useState("");

  const result = useQuery(ALL_AUTHORS);

  const [changeYear, results] = useMutation(EDIT_AUTHOR_BORNYEAR, {
    refetchQueries: [{ query: ALL_AUTHORS }],
    onError: (error) => {
      props.setError(error.graphQLErrors[0].message);
    },
  });

  useEffect(() => {
    if (results.data && results.data.editAuthor === null) {
      console.log("Something went wrong when changing birth year");
      console.log(results.data);
      console.log(results.data.editAuthor);
    }
  }, [results.data]);

  if (!props.show) return null;

  const handleSubmit = async (event) => {
    event.preventDefault();
    changeYear({ variables: { name, born } });
    setName("");
    setBorn("");
  };

  return (
    <div>
      <div>
        <h3>Edit birthyear</h3>
        <form onSubmit={handleSubmit}>
          <div>
            name
            <Select
              styles={{
                control: (base, _state) => ({
                  ...base,
                  height: "30px",
                }),
              }}
              defaultValue={name}
              onChange={({ value }) => setName(value)}
              options={result.data.allAuthors.map((a) => ({
                value: a.name,
                label: a.name,
              }))}
            ></Select>
          </div>
          <div>
            born
            <input
              type="number"
              value={born}
              onChange={({ target }) => setBorn(Number(target.value))}
            ></input>
          </div>
          <button type="submit">update author</button>
        </form>
      </div>
    </div>
  );
}; //end

export default BirthEdit;
