import React, { useState } from "react";
import { useQuery } from "@apollo/client";
import { ALL_BOOKS } from "../queries";

const Books = (props) => {
  const [genreSelector, setGenreSelector] = useState(null);

  const result = useQuery(ALL_BOOKS);

  let genres = [];
  //let books = result.data.allBooks;
  result.data.allBooks.forEach((book) => {
    book.genres.forEach((genre) =>
      genres.includes(genre) ? null : genres.push(genre)
    );
  });

  if (result.loading) {
    return <div>loading...</div>;
  }

  if (!props.show) {
    return null;
  }

  const handleGenreSelect = async (event) => {
    event.preventDefault();

    if (event.target.value === "all genres") {
      setGenreSelector(null);
      return;
    }
    setGenreSelector(event.target.value);
  };

  return (
    <div>
      <h2>Books</h2>
      <div>
        <p>placeholder </p>
      </div>

      <table cellSpacing="5">
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {books.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
              <td>{a.genres.join(", ")} </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h4>select genre</h4>
      <div>
        <button value={"all genres"} onClick={handleGenreSelect}>
          {"all genres"}
        </button>
        {genres.sort().map((g) => (
          <button key={g} value={g} onClick={handleGenreSelect}>
            {g}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Books;
