import React, { useEffect, useState } from "react";
import { useLazyQuery, useQuery } from "@apollo/client";
import { LOGGED_USER, ALL_BOOKS, ALLBOOKSGENRES } from "../queries";

const Recommendations = (props) => {
  const [books, setBooks] = useState([]);

  const result = useQuery(LOGGED_USER);
  const [getBooks, booksResult] = useLazyQuery(ALLBOOKSGENRES);

  const genre =
    result.data && result.data.me ? result.data.me.favoriteGenre : null;

  useEffect(() => {
    if (result.data) {
      getBooks({ variables: { genre } });
    }
  }, [result.data]); //eslint-disable-line

  useEffect(() => {
    if (booksResult.data) {
      setBooks(booksResult.data.allBooks);
    }
  }, [booksResult.data]);

  if (result.loading) {
    return <div>Loading..</div>;
  }

  if (!props.show) {
    return null;
  }

  return (
    <div>
      <h2>Recommendations</h2>
      <div>
        <p>
          books in your favorite genre <strong></strong>{" "}
        </p>
      </div>
      <table>
        <tbody>
          <tr>
            <th>title</th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {books.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Recommendations;
