import React, { useEffect, useState } from "react";
import { useQuery, useLazyQuery } from "@apollo/client";
import { ALL_BOOKS, BOOKS_GENRE, LOGGED_USER } from "../queries";

const Recommendations = (props) => {
  const [getBooks, result] = useLazyQuery(ALL_BOOKS);

  useEffect(() => {
    getBooks({ variables: { genre: props.genre } });
  }, [props.genre, getBooks]);

  if (!props.show || !props.genre || !result.data) return null;
  console.log("current user favorite genre:", props.genre);
  console.log(result.data);
  const books = result.data.allBooks;

  return (
    <div>
      <h2>Recommendations</h2>
      <div>
        <p>
          books in your favorite genre <strong></strong>
        </p>
      </div>
      <table>
        <tbody>
          <tr>
            <th>title</th>
            <th>author</th>
            <th>published</th>
            <th>genres</th>
          </tr>
          {books.map((a) => (
            <tr key={a.title}>
              <td> {a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
              <td>{a.genres}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Recommendations;
